}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-1.5 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
DialogHeader.displayName = "DialogHeader"

const DialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
DialogFooter.displayName = "DialogFooter"

const DialogTitle = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Title
    ref={ref}
    className={cn(
      "text-lg font-semibold leading-none tracking-tight",
      className
    )}
    {...props}
  />
))
DialogTitle.displayName = DialogPrimitive.Title.displayName

const DialogDescription = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
DialogDescription.displayName = DialogPrimitive.Description.displayName

export {
  Dialog,
  DialogPortal,
  DialogOverlay,
  DialogClose,
  DialogTrigger,
  DialogContent,
  DialogHeader,
  DialogFooter,
  DialogTitle,
  DialogDescription,
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import { pgTable, text, varchar, integer, real, timestamp, jsonb, index, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const CATEGORIES = [
  "Budget",
  "Standard", 
  "Godly",
  "Ancient",
  "Bundles",
  "Royal"
] as const;

export type Category = typeof CATEGORIES[number];

export const RARITIES = [
  "Common",
  "Uncommon", 
  "Rare",
  "Legendary",
  "Godly",
  "Ancient",
  "Chroma"
] as const;

export type Rarity = typeof RARITIES[number];

export const ORDER_STATUSES = [
  "pending",
  "awaiting_payment",
  "payment_uploaded",
  "confirmed",
  "processing",
  "completed",
  "cancelled"
] as const;

export type OrderStatus = typeof ORDER_STATUSES[number];

export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique().notNull(),
  password: varchar("password").notNull(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  robloxUsername: varchar("roblox_username"),
  profileImageUrl: varchar("profile_image_url"),
  isAdmin: boolean("is_admin").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).omit({ id: true, createdAt: true, updatedAt: true });
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type UpsertUser = typeof users.$inferInsert;

export const products = pgTable("products", {
  id: varchar("id").primaryKey(),
  name: text("name").notNull(),
  price: real("price").notNull(),
  category: text("category").notNull(),
  rarity: text("rarity").notNull(),
  description: text("description"),
  imageUrl: text("image_url"),
  inStock: integer("in_stock").default(1),
});

export const insertProductSchema = createInsertSchema(products).omit({ id: true });
export type InsertProduct = z.infer<typeof insertProductSchema>;
export type Product = typeof products.$inferSelect;

export const cartItems = pgTable("cart_items", {
  id: varchar("id").primaryKey(),
  productId: varchar("product_id").notNull(),
  quantity: integer("quantity").notNull().default(1),
  sessionId: varchar("session_id").notNull(),
});

export const insertCartItemSchema = createInsertSchema(cartItems).omit({ id: true });
export type InsertCartItem = z.infer<typeof insertCartItemSchema>;
export type CartItem = typeof cartItems.$inferSelect;

export const orders = pgTable("orders", {
  id: varchar("id").primaryKey(),
  userId: varchar("user_id"),
  phone: text("phone").notNull(),
  robloxUsername: text("roblox_username").notNull(),
  paymentMethod: text("payment_method").notNull(),
  paymentReference: text("payment_reference"),
  totalAmount: real("total_amount").notNull(),
  status: text("status").notNull().default("pending"),
  items: text("items").notNull(),
  receiptUrl: text("receipt_url"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertOrderSchema = createInsertSchema(orders).omit({ id: true, createdAt: true, updatedAt: true });
export type InsertOrder = z.infer<typeof insertOrderSchema>;
export type Order = typeof orders.$inferSelect;

export const chatMessages = pgTable("chat_messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  orderId: varchar("order_id").notNull(),
  senderId: varchar("sender_id").notNull(),
  senderType: varchar("sender_type").notNull(),
  message: text("message").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertChatMessageSchema = createInsertSchema(chatMessages).omit({ id: true, createdAt: true });
export type InsertChatMessage = z.infer<typeof insertChatMessageSchema>;
export type ChatMessage = typeof chatMessages.$inferSelect;

export interface CartItemWithProduct extends CartItem {
  product: Product;
}

export const checkoutFormSchema = z.object({
  phone: z.string().min(10, "Please enter a valid phone number").max(15, "Phone number is too long"),
  robloxUsername: z.string().min(3, "Roblox username must be at least 3 characters"),
  paymentMethod: z.enum(["vodafone_cash", "instapay"]),
  paymentReference: z.string().optional(),
});

export type CheckoutFormData = z.infer<typeof checkoutFormSchema>;

export const contactFormSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  robloxUsername: z.string().optional(),
  message: z.string().min(10, "Message must be at least 10 characters"),
});

export type ContactFormData = z.infer<typeof contactFormSchema>;

export const registerFormSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  firstName: z.string().min(2, "First name must be at least 2 characters"),
  lastName: z.string().optional(),
  robloxUsername: z.string().min(3, "Roblox username must be at least 3 characters"),
});

export type RegisterFormData = z.infer<typeof registerFormSchema>;

export const loginFormSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

export type LoginFormData = z.infer<typeof loginFormSchema>;

server/static.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");
  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

client/src/pages/product.tsx.content:
import { useParams, Link } from "wouter";
import { ArrowLeft, ShoppingCart, Zap, Shield, Package, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { ProductCard } from "@/components/product-card";
import { useCart } from "@/lib/cart-context";
import { useProduct, useProducts } from "@/hooks/use-products";
import { rarityColors } from "@/lib/products";
import { useToast } from "@/hooks/use-toast";
import { useMemo } from "react";

export default function ProductPage() {
  const { id } = useParams<{ id: string }>();
  const { addItem } = useCart();
  const { toast } = useToast();

  const { data: product, isLoading } = useProduct(id || "");
  const { data: allProducts = [] } = useProducts();

  const relatedProducts = useMemo(() => {
    if (!product) return [];
    return allProducts
      .filter(
        (p) =>
          p.id !== product.id &&
          (p.category === product.category || p.rarity === product.rarity)
      )
      .slice(0, 4);
  }, [product, allProducts]);

  if (isLoading) {
    return (
      <div className="flex min-h-[60vh] items-center justify-center">
        <div className="text-center">
          <Loader2 className="mx-auto mb-4 h-8 w-8 animate-spin text-primary" />
          <p className="text-muted-foreground">Loading product...</p>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container mx-auto flex min-h-[60vh] flex-col items-center justify-center px-4 py-16 text-center">
        <Package className="mb-4 h-16 w-16 text-muted-foreground" />
        <h1 className="mb-2 text-2xl font-bold">Product Not Found</h1>
        <p className="mb-6 text-muted-foreground">
          The item you're looking for doesn't exist or has been removed.
        </p>
        <Link href="/shop">
          <Button>
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Shop
          </Button>
        </Link>
      </div>
    );
  }

  const handleAddToCart = () => {
    addItem(product);
    toast({
      title: "Added to cart",
      description: `${product.name} has been added to your cart.`,
    });
  };

  const rarityClass = rarityColors[product.rarity] || rarityColors.Common;

  return (
    <div className="min-h-screen py-8">
      <div className="container mx-auto px-4">
        <nav className="mb-8 flex items-center gap-2 text-sm text-muted-foreground">
          <Link href="/" className="hover:text-foreground">
            Home
          </Link>
          <span>/</span>
          <Link href="/shop" className="hover:text-foreground">
            Shop
          </Link>
          <span>/</span>
          <Link
            href={`/shop?category=${product.category}`}
            className="hover:text-foreground"
          >
            {product.category}
          </Link>
          <span>/</span>
          <span className="text-foreground">{product.name}</span>
        </nav>

        <div className="grid gap-8 lg:grid-cols-2 lg:gap-12">
          <div className="relative aspect-square overflow-hidden rounded-lg border border-border bg-muted/30">
            <div className="flex h-full items-center justify-center bg-gradient-to-br from-muted/50 to-muted">
              {product.imageUrl ? (
                <img 
                  src={product.imageUrl} 
                  alt={product.name}
                  className="h-full w-full object-contain p-4"
                  onError={(e) => {
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                    target.nextElementSibling?.classList.remove('hidden');
                  }}
                />
              ) : null}
              <div className={`text-center ${product.imageUrl ? 'hidden' : ''}`}>
                <div className="mb-4 text-8xl font-bold text-primary/20">
                  {product.name.charAt(0)}
                </div>
                <p className="text-lg text-muted-foreground">MM2 Item</p>
              </div>
            </div>
            <Badge className={`absolute right-4 top-4 ${rarityClass}`}>
              {product.rarity}
            </Badge>
          </div>

          <div className="flex flex-col">
            <div className="mb-2 flex items-center gap-2">
              <Badge variant="outline">{product.category}</Badge>
              <Badge className={rarityClass}>{product.rarity}</Badge>
              {product.inStock === 0 && (
                <Badge variant="destructive">Out of Stock</Badge>
              )}
            </div>

            <h1
              className="mb-4 font-heading text-3xl font-bold md:text-4xl"
              data-testid="text-product-title"
            >
              {product.name}
            </h1>

            <p className="mb-6 text-lg text-muted-foreground" data-testid="text-product-description">
              {product.description}
            </p>

            <div className="mb-6">
              <span
                className="font-heading text-4xl font-bold text-primary md:text-5xl"
                data-testid="text-product-price"
              >
                {product.price}{" "}
                <span className="text-2xl text-muted-foreground">EGP</span>
              </span>
            </div>

            <div className="mb-6 flex flex-col gap-3 sm:flex-row">
              <Button
                size="lg"
                className="flex-1 gap-2 font-display uppercase tracking-wide"
                onClick={handleAddToCart}
                disabled={product.inStock === 0}
                data-testid="button-add-to-cart"
              >
                <ShoppingCart className="h-5 w-5" />
                {product.inStock === 0 ? "Out of Stock" : "Add to Cart"}
              </Button>
              <Link href="/cart" className="flex-1">
                <Button
                  size="lg"
                  variant="secondary"
                  className="w-full gap-2 font-display uppercase tracking-wide"
                  onClick={handleAddToCart}
                  disabled={product.inStock === 0}
                  data-testid="button-buy-now"
                >
                  Buy Now
                </Button>
              </Link>
            </div>

            <Separator className="my-6" />

            <div className="grid grid-cols-2 gap-4">
              <div className="flex items-center gap-3 rounded-lg border border-border bg-card p-3">
                <Zap className="h-5 w-5 text-primary" />
                <div>
                  <p className="font-semibold">Instant Delivery</p>
                  <p className="text-sm text-muted-foreground">5-30 minutes</p>
                </div>
              </div>
              <div className="flex items-center gap-3 rounded-lg border border-border bg-card p-3">
                <Shield className="h-5 w-5 text-primary" />
                <div>
                  <p className="font-semibold">Secure Payment</p>
                  <p className="text-sm text-muted-foreground">100% Safe</p>
                </div>
              </div>
            </div>

            <div className="mt-6 rounded-lg border border-border bg-card p-4">
              <h4 className="mb-3 font-display font-semibold">
                Accepted Payment Methods
              </h4>
              <div className="flex flex-wrap gap-3">
                <div className="flex items-center gap-2 rounded-md bg-muted/50 px-3 py-2">
                  <div className="flex h-6 w-6 items-center justify-center rounded-full bg-red-600 text-xs font-bold text-white">
                    V
                  </div>
                  <span className="text-sm">Vodafone Cash</span>
                </div>
                <div className="flex items-center gap-2 rounded-md bg-muted/50 px-3 py-2">
                  <div className="flex h-6 w-6 items-center justify-center rounded-full bg-blue-600 text-xs font-bold text-white">
                    I
                  </div>
                  <span className="text-sm">InstaPay</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {relatedProducts.length > 0 && (
          <section className="mt-16">
            <h2 className="mb-8 font-heading text-2xl font-bold uppercase tracking-wider">
              You May Also Like
            </h2>
            <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-4">
              {relatedProducts.map((p) => (
                <ProductCard key={p.id} product={p} />
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  );
}

