                <div className="space-y-2">
                  <Label>Rarity</Label>
                  <Select value={newProduct.rarity} onValueChange={(v) => setNewProduct({...newProduct, rarity: v})}>
                    <SelectTrigger data-testid="select-new-product-rarity">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {RARITIES.map(r => (
